import React, { Component } from 'react';
import { connect } from 'react-redux';
import Config from '../../Config.js';
import { userAction } from '../../_actions';

class LogIn extends Component {
    constructor(props) {
        super(props);
        this.props.dispatch(userAction.userLogout());
        this.state = {
            username: '',
            password: '',
            submitted: false
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { username, password } = this.state;
        //if (username && password) {
         //   this.props.dispatch(userAction.userLogin(username, password));
        //}
    }

    render(){
        const { username, password, submitted } = this.state;
        return (
            <div>
                <h3 style={{color: Config.colors.theme}}>User Login</h3>
                <form class="form-group" onSubmit={this.handleSubmit}>
                    <div>
                        <input type="text" name="username" className="form-control" placeholder="Email Address"/>
                    </div>
                    <div>
                        <input type="password" name="password"  className="form-control" placeholder="Password"/>
                    </div>
                    {/*<p>
                        <label>
                            <input type="checkbox" value="remember-me" id="rememberMe"
                                   name="rememberMe"/>
                            Remember me
                        </label>
                     </p>*/
                    }
                    <div>
                        {this.props.loggingIn &&
                            <img src="data:image/gif;base64," />
                        }
                        <button className="btn btn-primary" type="submit">
                            Login
                        </button>
                    </div>
                </form>
            </div>
        );
    }
}

function mapStateToProps(state) {
    const { loggingIn } = state.authentication;
    return {
        loggingIn
    };
}

export default connect(mapStateToProps)(LogIn);